import React, { useState } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { isAuthenticated } from "../auth/helper";

const AddCategory = () => {

    const [name, setName] = useState("");
    const [error, setError] = useState("false");
    const [success, setSuccess] = useState("false");

    const { user, token } = isAuthenticated();

    const myCategoryForm = () => {
        return (
            <form>
                <div className="form-group">
                    <p className="lead">Enter the Category</p>
                    <input type="text" className="form-control my-3" autoFocus required placeholder="Eg. Summer" />
                </div>
            </form>
        )
    }

    return (
        <Base title="Create a category here" description="Add a new category for tshirts" className="container bg-info p-4">
            <div className="row bg-white rounded">
                <div className="col-md-8 offset-md-2">Hello there</div>
            </div>
        </Base>
    );
};

export default AddCategory;
