import React, { useState } from "react";
import Base from "../core/Base";
import { isAuthenticated } from "../auth/helper";
import { Link } from "react-router-dom";
import { createProduct } from "./helper/adminapicall";

const AddProduct = () => {

    const [name, setName] = useState("");

    const handleChange = (event) => {
        //
    };

    const onSubmit = (event) => {
        event.preventDefault();
    };

    const createProductForm = () => (
        <form >
            <span>Post photo</span>
            <div className="form-group">
                <label className="btn btn-block btn-success">
                    <input
                        onChange={handleChange("photo")}
                        type="file"
                        name="photo"
                        accept="image"
                        placeholder="choose a file"
                    />
                </label>
            </div>
            <div className="form-group">
                <input
                    onChange={handleChange("name")}
                    name="photo"
                    className="form-control"
                    placeholder="Name"
                    value={name}
                />
            </div>
            <div className="form-group">
                <textarea
                    onChange={handleChange("description")}
                    name="photo"
                    className="form-control"
                    placeholder="Description"
                    //value={description}
                />
            </div>
            <div className="form-group">
                <input
                    onChange={handleChange("price")}
                    type="number"
                    className="form-control"
                    placeholder="Price"
                    //value={price}
                />
            </div>
            <div className="form-group">
                <select
                    onChange={handleChange("category")}
                    className="form-control"
                    placeholder="Category"
                >
                    <option>Select</option>
                    <option value="a">a</option>
                    <option value="b">b</option>
                </select>
            </div>
            <div className="form-group">
                <input
                    onChange={handleChange("quantity")}
                    type="number"
                    className="form-control"
                    placeholder="Quantity"
                    //value={stock}
                />
            </div>
      
            <button type="submit" onClick={onSubmit} className="btn btn-outline-success">
                Create Product
      </button>
        </form>
    );
    
    return (
        <Base title="Add a product here" description="Welcome to product craetion section" className="container bg-info p-4">
            {createProductForm()}
            <Link to="/admin/dashboard" className="btn btn-md btn-dark my-3 mb-3">Admin Home</Link>
        </Base>
    );
};

export default AddProduct;
