import React, { useEffect, useState } from "react";
import "../styles.css";
import {API} from "../backend";
import Base from "./Base";
import Card from "./Card";
import { loadCart } from "./helper/cartHelper";
import StripeCheckout from "./StripeCheckout";
import Paymentb from "./payment";


const Cart = () => {

    const [products, setProducts] = useState([]);
    const [reload, setReload] = useState(false);

    useEffect(() => {
        setProducts(loadCart());
    }, [reload]);

    const loadAllCart = products => {
        return (
            <div>
                <h4>This section is to load the products.</h4>
                {products.map((product, index) => {
                    return (
                        <Card
                            key={index}
                            product={product}
                            addToCart={false}
                            removeFromCart={true}
                            setReload={setReload}
                            reload={reload}
                        />
                    );
                } )}
            </div>
        );
    };

    const loadCheckouts = () => {
        return (
            <div>
                <h4><StripeCheckout products={products} setReload={setReload} reload={reload}/></h4>
            </div>
        );
    }

    return (
        <Base title="Cart Page" description="This is your Cart">
            <div className="row text-center" >
                <div className="col-6">{products.length > 0 ? loadAllCart(products) : (<h3>No products in cart</h3>)}</div>
                <div className="col-6"><Paymentb products={products} /></div>
            </div>
        </Base>
    );
};

export default Cart;
