import React, { useEffect, useState } from "react";
import "../styles.css";
import {API} from "../backend";
import Base from "./Base";
import Card from "./Card";
import { loadCart } from "./helper/cartHelper";

const Cart = () => {
    
    const loadAllCart = () => {
        return (
            <div>
                <h4>This section is to load the products.</h4>
            </div>
        );
    };

    const loadCheckouts = () => {
        return (
            <div>
                <h4>This section is for checkout.</h4>
            </div>
        );
    }

    return (
        <Base title="Cart Page" description="This is your Cart">
            <div className="row" >
                <div className="col-6">{loadAllCart()}</div>
                <div className="col-6">{loadCheckouts()}</div>
            </div>
        </Base>
    );
};

export default Cart;
