import React, { useState } from "react";
import Base from "../core/Base";
import { Link, Redirect } from "react-router-dom";
import { signin } from "../auth/helper";

const Signin = () => {

    const [values, setValues] = useState({
        email: "",
        password: "",
        error: "",
        loading: false,
        didRedirect: false
    });

    const { email, password, error, loading, didRedirect } = values;

    const handleChange = name => event => {
        setValues({ ...values, error: false, [name]: event.target.value });
    };

    const successMessage = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <div className="alert alert-success" style={{ display: success ? "" : "none" }}>
            New account was created successfully, Please <Link to="/signin">Login here</Link>
          </div>
        </div>
      </div>
      );
  }

  const errorMessage = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <div className="alert alert-danger" style={{ display: error ? "" : "none" }}>
            {error}
          </div>
        </div>
      </div>
      );
  }

    const signInForm = () => {
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <form>
                        <div className="form-group">
                            <label className="text-light">Email</label>
                            <input onClick={handleChange("email")} value={email} className="form-control" type="email" />
                        </div>
                        <div className="form-group">
                            <label className="text-light">Password</label>
                            <input onClick={handleChange("password")} value={password} className="form - control" type="password" />
                        </div>    
                        <div className="d-grid gap-2 py-3">
                            <button className="btn btn-success " type="submit">Submit</button>
                        </div>
                    </form>
                </div>
            </div>
        )
    };

    return (
        <Base title="Signin page" description="A page for user to sign in!">
            {signInForm()}
        </Base>
    )
};

export default Signin;
