import React, { useState, useEffect } from "react";
import Base from "../core/Base";
import { isAuthenticated } from "../auth/helper";
import { Link, Redirect } from "react-router-dom";
import { createProduct, getCategories } from "./helper/adminapicall";

const AddProduct = () => {

    const { user, token } = isAuthenticated();

    const [values, setValues] = useState({
        name: "",
        description: "",
        price: "",
        stock: "",
        photo: "",
        categories: [],
        category: "",
        loading: false,
        error: "",
        createdProduct: "",
        getRedirect: false,
        formData: ""
    });

    const { name, description, price, stock, categories, category, loading, error, createdProduct, getRedirect, formData } = values;

    const preload = () => {
        getCategories().then(data => {
            if (data.error) {
                setValues({ ...values, error: data.error });
            } else {
                setValues({ ...values, categories: data, formData: new FormData() });
                console.log(categories);
            }
        });
    };

    useEffect(() => {
        preload();
    }, []);

    const successMessage = () => (
        <div className="alert alert-success mt-3" style={{ display: createdProduct ? "" : "none" }}>
            <h4>{createdProduct} created successfuly</h4>
        </div>
    );

    const warningMessage = () => {
        if (error) {
        return <h4 className="text-danger">Failed to create Product</h4>;
        }
    };

    const afterSuccess = () => {
        if (createdProduct) {
            return setTimeout(() => {  return (<Redirect to="/admin/dashboard" />) }, 2000);
        }
    };

    const handleChange = name => event => {
        const value = name === "photo" ? event.target.files[0] : event.target.value;
        formData.set(name, value);
        setValues({ ...values, [name]: value });
    };

    const onSubmit = (event) => {
        event.preventDefault();
        setValues({ ...values, error: "" });
        createProduct(user._id, token, formData).then(data => {
            if (data.error) {
                setValues({ ...values, error: data.error });
            } else {
                setValues({
                    ...values,
                    name: "",
                    description: "",
                    price: "",
                    photo: "",
                    stock: "",
                    loading: false,
                    createdProduct: data.name
                });
            }
        })
    };

    const createProductForm = () => (
        <form>
            <span>Post photo</span>
            <div className="form-group mb-3">
                <label className="btn d-grid gap-2 btn-success px-4">
                    <input 
                        onChange={handleChange("photo")}
                        type="file"
                        name="photo"
                        accept="image"
                        placeholder="choose a file"
                    />
                </label>
            </div>
            <div className="form-group mb-3">
                <input
                    onChange={handleChange("name")}
                    name="photo"
                    className="form-control"
                    placeholder="Name"
                    value={name}
                />
            </div>
            <div className="form-group mb-3">
                <textarea
                    onChange={handleChange("description")}
                    name="photo"
                    className="form-control"
                    placeholder="Description"
                    value={description}
                />
            </div>
            <div className="form-group mb-3">
                <input
                    onChange={handleChange("price")}
                    type="number"
                    className="form-control"
                    placeholder="Price"
                    value={price}
                />
            </div>
            <div className="form-group mb-3">
                <select
                    onChange={handleChange("category")}
                    className="form-control"
                    placeholder="Category"
                >
                    <option>Select Category</option>
                    {categories && 
                        categories.map((cate, index) => (
                            <option value={cate._id}>{cate.name}</option>
                    ))
                    }
                </select>
            </div>
            <div className="form-group mb-3">
                <input
                    onChange={handleChange("stock")}
                    type="number"
                    className="form-control"
                    placeholder="Quantity"
                    value={stock}
                />
            </div>
      
            <button type="submit" onClick={onSubmit} className="btn btn-outline-success mb-3">
                Create Product
      </button>
        </form>
    );
    
    return (
        <Base title="Add a product here" description="Welcome to product craetion section" className="container bg-info p-4">
            <div className="row bg-dark text-white rounded">
                <div className="col-md-8 offset-md-2">
                    {successMessage()}
                    {warningMessage()}
                    {afterSuccess()}
                    {createProductForm()}
                </div>
            </div>
            <Link to="/admin/dashboard" className="btn btn-md btn-dark mt-3">Admin Home</Link>
        </Base>
    );
};

export default AddProduct;
