import React, { useState } from "react";
import Base from "../core/Base";
import { isAuthenticated } from "../auth/helper";
import { Link } from "react-router-dom";
import { createProduct } from "./helper/adminapicall";

const AddProduct = () => {

    const [values, setValues] = useState({
        name: "",
        description: "",
        price: "",
        stock: ""
    });

    const { name, description, price, stock } = values;

    const handleChange = (event) => {
        //
    };

    const onSubmit = (event) => {
        event.preventDefault();
    };

    const createProductForm = () => (
        <form>
            <span>Post photo</span>
            <div className="form-group mb-3">
                <label className="btn d-grid gap-2 btn-success">
                    <input
                        onChange={handleChange("photo")}
                        type="file"
                        name="photo"
                        accept="image"
                        placeholder="choose a file"
                    />
                </label>
            </div>
            <div className="form-group mb-3">
                <input
                    onChange={handleChange("name")}
                    name="photo"
                    className="form-control"
                    placeholder="Name"
                    value={name}
                />
            </div>
            <div className="form-group mb-3">
                <textarea
                    onChange={handleChange("description")}
                    name="photo"
                    className="form-control"
                    placeholder="Description"
                    value={description}
                />
            </div>
            <div className="form-group mb-3">
                <input
                    onChange={handleChange("price")}
                    type="number"
                    className="form-control"
                    placeholder="Price"
                    value={price}
                />
            </div>
            <div className="form-group mb-3">
                <select
                    onChange={handleChange("category")}
                    className="form-control"
                    placeholder="Category"
                >
                    <option>Select</option>
                    <option value="a">a</option>
                    <option value="b">b</option>
                </select>
            </div>
            <div className="form-group mb-3">
                <input
                    onChange={handleChange("quantity")}
                    type="number"
                    className="form-control"
                    placeholder="Quantity"
                    value={stock}
                />
            </div>
      
            <button type="submit" onClick={onSubmit} className="btn btn-outline-success">
                Create Product
      </button>
        </form>
    );
    
    return (
        <Base title="Add a product here" description="Welcome to product craetion section" className="container bg-info p-4">
            <div className="row bg-dark text-white rounded">
                <div className="col-md-8 offset-md-2">
                    {createProductForm()}
                </div>
            </div>
            <Link to="/admin/dashboard" className="btn btn-md btn-dark mb-3">Admin Home</Link>
        </Base>
    );
};

export default AddProduct;
