import React from "react";
import ImageHelper from "./helper/Imagehelper";

const Card = ({
  product,
  addToCart = true,
  removeFromCart = false
}) => {

  const cardTitle = product ? product.name : "Default Photo";
  const cardDescription = product ? product.description : "Default Photo";
  const cardPrice = product ? product.price : "Default Photo";


  const showAddToCart = (addToCart) => {
    return (
      addToCart && (
        <button
          onClick={() => { }}
          className="btn btn-block btn-outline-success mt-2 mb-2"
        >
          Add to Cart
        </button>
      )
    );
  };

  const showRemoveFromCart = (removeFromCart) => {
    return (
      removeFromCart && (
        <button
          onClick={() => { }}
          className="btn btn-block btn-outline-danger mt-2 mb-2"
        >
          Remove from cart
        </button>
      )
    );
  };

  return (
    <div className="card text-white bg-dark border border-info ">
      <div className="card-header lead">A photo from pexels</div>
      <div className="card-body">
        <ImageHelper product={product} />
        <p className="lead bg-success font-weight-normal text-wrap">
          this photo looks great
        </p>
        <p className="btn btn-success rounded  btn-sm px-4">$ 5</p>
        <div className="row">
          <div className="col-12">
            {showAddToCart(addToCart)}
          </div>
          <div className="col-12">
            {showRemoveFromCart(removeFromCart)}
          </div>
        </div>
      </div>
    </div>
  );
};
  
export default Card;
