import React, { useState } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { isAuthenticated } from "../auth/helper";
import { createCategory } from "./helper/adminapicall";


const AddCategory = () => {

    const [name, setName] = useState("");
    const [error, setError] = useState("false");
    const [success, setSuccess] = useState("false");

    const { user, token } = isAuthenticated();

    const handleChange = (event) => {
        setError("");
        setName(event.target.value);
    };

    const onSubmit = (event) => {
        event.preventDefault();
        setError("");
        setSuccess(false);
        //backend request fired
        createCategory(user._id, token, { name }).then(data => {
            if (data.err) {
                setError(true);
            } else {
                setError("");
                setSuccess(true);
            }
        })
    };

    const myCategoryForm = () => {
        return (
            <form>
                <div className="form-group">
                    <p className="lead"><h4>Enter the Category</h4></p>
                    <input type="text" className="form-control my-3" onChange={handleChange} value={name} autoFocus required placeholder="Eg. Summer" />
                    <button className="btn btn-outline-info mb-3" onClick={onSubmit}>Create</button>
                </div>
            </form>
        );
    };

    const goBack = () => {
        return (
            <div className="mt-5">
                <Link className="btn btn-sm btn-info mb-3" to="/admin/dashboard">Admin Home</Link>
            </div>
        );
    };

    

    return (
        <Base title="Create a category here" description="Add a new category for tshirts" className="container bg-info p-4">
            <div className="row bg-white rounded">
                <div className="col-md-8 offset-md-2">
                    {myCategoryForm()}
                    {goBack()}
                </div>
            </div>
        </Base>
    );
};

export default AddCategory;
